mapper.select:
SELECT a_data.id, a_student.name, a_student.gender, a_student.school, a_student.grade, a_student.classes, a_student.duration, a_student.state, a_student.teacher, a_data.year, a_data.height, a_data.weight, a_data.vital_capacity, a_data.fivem, a_data.long_jump, a_data.reach, a_data.eightm, a_data.tenm, a_data.sit_ups, a_data.pull_up, a_data.score, a_data.first_check, a_data.second_check FROM a_data, a_student WHERE a_data.id = a_student.id AND a_student.state != '已毕业' AND a_data.id LIKE '%${id}%' AND a_student.name LIKE '%${name}%'
AND a_student.school = #{school}
AND a_data.year LIKE '%${year}%' ORDER BY a_data.year DESC, a_data.id limit #{page},#{rows}

SELECT count(*) FROM a_data, a_student WHERE a_data.id = a_student.id AND a_student.state != '已毕业' AND a_data.id LIKE '%${id}%' AND a_student.name LIKE '%${name}%'
AND a_student.school = #{school}
AND a_data.year LIKE '%${year}%'

